import React, { useEffect, useState } from "react";
import { Container, Row, Col, Button, Card } from "react-bootstrap";

const Home = () => {
  const [movies, setMovies] = useState([]);

  // Fetch movies from a public API (e.g., TMDB)
  useEffect(() => {
    fetch("https://api.themoviedb.org/3/trending/movie/day?api_key=YOUR_API_KEY")
      .then((response) => response.json())
      .then((data) => setMovies(data.results || []))
      .catch((error) => console.error("Error fetching movies:", error));
  }, []);

  return (
    <>
      {/* Hero Section */}
      <div className="hero-section text-center text-white d-flex align-items-center justify-content-center" style={{ height: "60vh", background: "url('/path/to/your/hero-image.jpg') center/cover no-repeat" }}>
        <div>
          <h1>Welcome to MovieInfo</h1>
          <p className="lead">Discover trending movies, search for your favorites, and more!</p>
          <Button variant="primary" size="lg">Explore Now</Button>
        </div>
      </div>

      {/* Trending Movies Section */}
      <Container className="mt-5">
        <h2 className="mb-4 text-center">Trending Movies</h2>
        <Row>
          {movies.slice(0, 6).map((movie) => (
            <Col md={4} className="mb-4" key={movie.id}>
              <Card>
                <Card.Img
                  variant="top"
                  src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
                  alt={movie.title}
                />
                <Card.Body>
                  <Card.Title>{movie.title}</Card.Title>
                  <Card.Text>{movie.overview.slice(0, 100)}...</Card.Text>
                  <Button variant="primary" href={`https://www.themoviedb.org/movie/${movie.id}`} target="_blank">
                    Learn More
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </>
  );
};

export default Home;
