import React, { useEffect, useState } from "react";
import { Container, Row, Col, Button, Card, Badge } from "react-bootstrap";

const Home = () => {
  const [movies, setMovies] = useState([]);

  // Fetch movies from the TMDB API
  useEffect(() => {
    fetch("https://api.themoviedb.org/3/trending/movie/day?api_key=de0755a70baeb4f4e1fc5cfc495702ea")
      .then((response) => response.json())
      .then((data) => setMovies(data.results || []))
      .catch((error) => console.error("Error fetching movies:", error));
  }, []);

  return (
    <>
      {/* Hero Section */}
      <div
        className="hero-section text-center text-white d-flex align-items-center justify-content-center"
        style={{
          height: "70vh",
          background: "url('/hero.jpg') center/cover no-repeat",
          backgroundSize: "cover",
          position: "relative",
        }}
      >
        <div
          style={{
            backgroundColor: "rgba(0, 0, 0, 0.6)",
            padding: "20px",
            borderRadius: "10px",
          }}
        >
          <h1 className="display-3">Welcome to MovieInfo</h1>
          <p className="lead">
            Discover trending movies, search for your favorites, and dive deep into their details!
          </p>
          <Button variant="primary" size="lg">
            Explore Now
          </Button>
        </div>
      </div>

      {/* Trending Movies Section */}
      <Container className="mt-5">
        <h2 className="mb-4 text-center">Trending Movies</h2>
        <Row>
          {movies.slice(0, 6).map((movie) => (
            <Col md={4} className="mb-4" key={movie.id}>
              <Card>
                <Card.Img
                  variant="top"
                  src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
                  alt={movie.title}
                />
                <Card.Body>
                  <Card.Title>{movie.title}</Card.Title>
                  <Card.Text>
                    <Badge bg="info" className="me-2">
                      ⭐ {movie.vote_average}
                    </Badge>
                    <Badge bg="secondary">
                      {new Date(movie.release_date).getFullYear()}
                    </Badge>
                  </Card.Text>
                  <Card.Text>{movie.overview.slice(0, 100)}...</Card.Text>
                  <Button
                    variant="primary"
                    href={`https://www.themoviedb.org/movie/${movie.id}`}
                    target="_blank"
                  >
                    Learn More
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>

      {/* More Movies Section */}
      <Container className="mt-5">
        <h2 className="mb-4 text-center">More Movies</h2>
        <Row>
          {movies.slice(6, 12).map((movie) => (
            <Col md={3} className="mb-4" key={movie.id}>
              <Card>
                <Card.Img
                  variant="top"
                  src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
                  alt={movie.title}
                />
                <Card.Body>
                  <Card.Title className="fs-6">{movie.title}</Card.Title>
                  <Button
                    variant="outline-primary"
                    href={`https://www.themoviedb.org/movie/${movie.id}`}
                    target="_blank"
                    size="sm"
                  >
                    Details
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>

      {/* Featured Movie Section */}
      {movies[0] && (
        <div
          className="featured-section text-white d-flex align-items-center justify-content-center"
          style={{
            height: "50vh",
            background: `url(https://image.tmdb.org/t/p/original/${movies[0].backdrop_path}) center/cover no-repeat`,
          }}
        >
          <div
            style={{
              backgroundColor: "rgba(0, 0, 0, 0.7)",
              padding: "20px",
              borderRadius: "10px",
            }}
          >
            <h2 className="text-center mb-3">Featured Movie</h2>
            <h3>{movies[0].title}</h3>
            <p className="lead">{movies[0].overview}</p>
            <Button
              variant="primary"
              href={`https://www.themoviedb.org/movie/${movies[0].id}`}
              target="_blank"
            >
              Watch Now
            </Button>
          </div>
        </div>
      )}
    </>
  );
};

export default Home;
